		/**
		 * Setup Actions
		 *
		 * @since     1.0.0
		 */
		public function setup_actions()
		{
			add_action(
				'elementor/editor/after_enqueue_scripts',
				function () {
					if (get_the_ID() == (int) $this->get_builder_id()) {
						wp_enqueue_script('kata-elementor-header-builder', Kata_Plus::$assets . '/js/backend/header-builder.js', ['jquery'], Kata_Plus::$version, false);
					}
				}
			);
		}

		/**
		 * Upload logo.
		 *
		 * @since   1.0.0
		 */
		public static function upload_logo()
		{
			$kata_options = get_option('kata_options');
			if ($kata_options == '') {
				$kata_options                      = get_option('kata_options');
				$kata_options['images']['logo']    = Kata_Plus_Helpers::insert_attachment(Kata_Plus::$assets . 'images/logo.svg');
				$kata_options['images']['404']     = Kata_Plus_Helpers::insert_attachment(Kata_Plus::$assets . 'images/404.svg');
				$kata_options['images']['favicon'] = Kata_Plus_Helpers::insert_attachment(Kata_Plus::$assets . 'images/favicon.png');
				update_option('kata_options', $kata_options);
			}
		}

		/**
		 * Enqueue Scripts
		 *
		 * @since     1.0.0
		 */
		public function enqueue_scripts()
		{
			if (class_exists('\Elementor\Plugin')) {
				$elementor = \Elementor\Plugin::instance();
				$elementor->frontend->enqueue_styles();
			}
			// wp_add_inline_script('kata-plus-template-manager', 'var kata_plus_this_page_name = "header"','before');
			$redirect_url = isset( $_SERVER['REDIRECT_URL'] ) ? $_SERVER['REDIRECT_URL'] : '';
			if ( ( \Elementor\Plugin::$instance->preview->is_preview_mode() && get_the_ID() == $this->get_builder_id() ) || strpos( $redirect_url, 'kata-header' ) ) {
				echo '<style>.elementor-container .elementor-widget-wrap > .elementor-widget {display: inline-block;width: auto;vertical-align: top;}.elementor-top-column:first-child .elementor-widget-wrap {display: flex;justify-content: left;}.elementor-top-column .elementor-widget-wrap {display: flex;justify-content:center;}.elementor-top-column:last-child .elementor-widget-wrap {display: flex;justify-content:flex-end;} ul.elementor-editor-element-settings.elementor-editor-section-settings { background-color: #f2ab3c !important; } ul.elementor-editor-element-settings.elementor-editor-section-settings li:hover { background-color: #f2df3c !important; } .elementor-editor-section-settings .elementor-editor-element-setting:first-child:before, .elementor-editor-section-settings .elementor-editor-element-setting:first-child:hover:before { border-right-color: #f2ab3c !important; } .elementor-editor-section-settings .elementor-editor-element-setting:last-child:after, .elementor-editor-section-settings .elementor-editor-element-setting:last-child:hover:after { border-left-color: #f2ab3c !important; } .elementor-section > .elementor-element-overlay:after { outline-color: #f2ab3c !important; }</style>';
			} else {
				echo '<style>.kata-header-wrap .elementor-container .elementor-widget-wrap > .elementor-widget {display: inline-block;width: auto;vertical-align: top;}.kata-hamburger-menu-template .elementor-container .elementor-widget-wrap > .elementor-widget { width: 100%; } .kata-header-wrap .elementor-top-column:first-child .elementor-widget-wrap {display: flex;justify-content: left;}.kata-header-wrap .elementor-top-column .elementor-widget-wrap {display: flex;justify-content:center;}.kata-header-wrap .elementor-top-column:last-child .elementor-widget-wrap{display: flex; justify-content: flex-end;}</style>';
			}

			if (class_exists('\ElementorPro\Plugin')) {
				$elementor_pro = \ElementorPro\Plugin::instance();
				$elementor_pro->enqueue_styles();
			}
		}
	} // end class

	Kata_Plus_Header_Builder::get_instance();
}
